tinymce.PluginManager.add('stuffbox', function(e) {
	editor.addCommand('InsertStuffBox', function() {
		//editor.execCommand('mceInsertContent', false, '<hr />');
		editor.execCommand('mceInsertContent', !1, '/*___*/');
	});

	editor.addButton('stuffbox', {
		text: 'Stuff box',
		icon: 'code',
		tooltip: 'Insert Stuff box',
		cmd: 'InsertStuffBox'
	});

	editor.addMenuItem('stuffbox', {
		icon: 'code',
		text: 'Stuff box',
		cmd: 'Insert Stuff box',
		context: 'insert'
	});
});